(()=>{"use strict";class e{static async showScores(){const t=await fetch("https://us-central1-js-capstone-backend.cloudfunctions.net/api/games/L1Z7c5x8dgBTEjM8pqtd/scores/");(await t.json()).result.forEach((t=>e.addScoresToList(t)))}static addScoresToList(e){const t=document.getElementById("scores-list"),s=document.createElement("li");t.appendChild(s),s.className="score";const n=document.createElement("p"),c=document.createElement("p");s.appendChild(n),s.appendChild(c),n.textContent=`${e.user}: `,c.textContent=` ${e.score}`}static clearFields(){document.getElementById("name-input").value="",document.getElementById("score-input").value=""}static deleteList(){document.getElementById("scores-list").innerText="",setTimeout(e.showScores,1e3)}static showAlert(e,t,s){const n=document.createElement("div");n.className=`alert alert-${t}`,n.appendChild(document.createTextNode(e)),document.getElementById("app-title").appendChild(n),setTimeout((()=>document.querySelector(".alert").remove()),s)}}document.addEventListener("DOMContentLoaded",e.showScores),document.querySelector("#form-section").addEventListener("submit",(t=>{t.preventDefault();const s=document.getElementById("name-input").value,n=document.getElementById("score-input").value;""===s||""===n?e.showAlert("Please fill in all fields","danger",1500):(class{static sendData(e,t){fetch("https://us-central1-js-capstone-backend.cloudfunctions.net/api/games/L1Z7c5x8dgBTEjM8pqtd/scores/",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({user:e,score:t})})}}.sendData(s,n),e.showAlert("Score Added","success",1500),e.clearFields(),setTimeout(e.deleteList,2e3))})),document.getElementById("refresh-btn").addEventListener("click",(()=>{e.showAlert("Loading Scores","loading",2700),setTimeout(e.deleteList,1e3)}))})();